import unittest

from hamming_code import decode, encode


class Test(unittest.TestCase):
    def test_hamming_code(self):
        self.assertEqual(encode("hey"), "000111111000111000000000000111111000000111000111000111111111111000000111")
        self.assertEqual(encode("The Sensei told me that i can do this kata"), "")
        self.assertEqual(encode("T3st"), "000111000111000111000000000000111111000000111111000111111111000000111111000111111111000111000000")
        self.assertEqual(encode("T?st!%"), "000111000111000111000000000000111111111111111111000111111111000000111111000111111111000111000000000000111000000000000111000000111000000111000111")

        self.assertEqual(decode("100111111000111001000010000111111000000111001111000111110110111000010111"), "hey")
        self.assertEqual(decode(""), "The Sensei told me that i can do this kata")
        self.assertEqual(decode("000111000111000111000010000000111111000000111111000111111111000000111111000111111111000111010000"), "T3st")
        self.assertEqual(decode("000111000111000111000001000000111111110111111111000111111111000000111111000111111111000111000000000000111000000000000111000000111000000111000111"), "T?st!%")


if __name__ == '__main__':
    unittest.main()
